import React, { useState } from 'react';
import './Welcome.css';

function Welcome() {
  const [isHistoryExpanded, setIsHistoryExpanded] = useState(false);

  const toggleHistoryMenu = () => {
    setIsHistoryExpanded(!isHistoryExpanded);
  };

  return (
    <div className="welcome-container">
      {/* Top Navigation Bar */}
      <header className="top-nav">
        {/* Removed the logo from here */}
        <div className="user-profile">
          <div className="dropdown-content">
            <a href="#">Profile</a>
            <a href="#">Settings</a>
            <a href="#">Logout</a>
          </div>
        </div>
      </header>

      {/* Side Navigation Menu */}
      <nav className="side-nav">
        <div className="sidebar-logo">
        </div>
        <ul>
          <li><a href="#" className="active">AI Hub</a></li>
          <li><a href="#">Home</a></li>
          <li className="expandable" onClick={toggleHistoryMenu}>
            <a href="#">History</a>
            <ul className={`submenu ${isHistoryExpanded ? 'visible' : ''}`}>
              <li><a href="#">Recent Searches</a></li>
              <li><a href="#">Saved Threads</a></li>
            </ul>
          </li>
          <li><a href="#">Discover</a></li>
          <li><a href="#">Example Prompts</a></li>
          <li><a href="#">Learn More</a></li>
        </ul>
      </nav>

      {/* Central Content Area */}
      <main className="content">
        <h1>PLACEHOLDER</h1>
        <div className="input-area">
          <input type="text" placeholder="Start a thread..." />
          <button>Submit as New Thread</button>
        </div>
      </main>

      {/* Footer */}
      <footer className="footer">
        <p>
          Disclaimer: This platform is for internal use only.  
          <a href="#"> PLACEHOLDER 1 </a> | 
          <a href="#"> PLACEHOLDER 2 </a>
        </p>
        <p>2025 © PLACEHOLDER Inc.</p>
      </footer>
    </div>
  );
}

export default Welcome;
