import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Welcome() {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [step, setStep] = useState(0); // Tracks the current step in the conversation
  const navigate = useNavigate();

  // Mock LLM questions
  const llmQuestions = [
    "Welcome! What area of your business would you like to optimize? (Finance, Compliance, Legal)",
    "Can you describe the specific processes or challenges you're facing?",
    "Thank you! Based on your inputs, we'll generate some recommendations. Click 'Get Started' to proceed."
  ];

  // Initial message from the LLM
  useEffect(() => {
    addMessage('AI', llmQuestions[0]);
  }, []);

  // Function to add a message to the chat
  const addMessage = (sender, text) => {
    setMessages((prevMessages) => [...prevMessages, { sender, text }]);
  };

  // Function to handle user input
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!userInput.trim()) return;

    // Add user's message to the chat
    addMessage('User', userInput);
    setUserInput('');
    setIsLoading(true);

    // Simulate LLM response delay
    setTimeout(() => {
      // Add the next LLM question
      if (step < llmQuestions.length - 1) {
        addMessage('AI', llmQuestions[step + 1]);
        setStep(step + 1);
      }
      setIsLoading(false);
    }, 1000); // Simulate 1-second delay
  };

  // Function to proceed to the Ideation screen
  const handleGetStarted = () => {
    navigate('/ideation');
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Welcome to AI Efficiency Gains Portal</h1>
      <div
        style={{
          border: '1px solid #ccc',
          borderRadius: '5px',
          padding: '10px',
          height: '300px',
          overflowY: 'scroll',
          marginBottom: '10px',
          backgroundColor: '#f9f9f9'
        }}
      >
        {messages.map((msg, index) => (
          <div
            key={index}
            style={{
              textAlign: msg.sender === 'User' ? 'right' : 'left',
              margin: '10px 0',
              padding: '5px',
              backgroundColor: msg.sender === 'User' ? '#e3f2fd' : '#fff',
              borderRadius: '5px',
              maxWidth: '70%',
              marginLeft: msg.sender === 'User' ? 'auto' : '0',
              marginRight: msg.sender === 'AI' ? 'auto' : '0'
            }}
          >
            <strong>{msg.sender}:</strong> {msg.text}
          </div>
        ))}
        {isLoading && (
          <div style={{ textAlign: 'left', fontStyle: 'italic', color: '#666' }}>
            AI is typing...
          </div>
        )}
      </div>
      <form onSubmit={handleSubmit} style={{ marginTop: '10px', display: 'flex', gap: '10px' }}>
        <input
          type="text"
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          placeholder="Type your response..."
          disabled={isLoading}
          style={{ flex: 1, padding: '5px', borderRadius: '5px', border: '1px solid #ccc' }}
        />
        <button
          type="submit"
          disabled={isLoading}
          style={{ padding: '5px 10px', borderRadius: '5px', border: 'none', backgroundColor: '#007bff', color: '#fff' }}
        >
          Send
        </button>
      </form>
      {step === llmQuestions.length - 1 && (
        <button
          onClick={handleGetStarted}
          style={{
            marginTop: '20px',
            padding: '10px 20px',
            borderRadius: '5px',
            border: 'none',
            backgroundColor: '#28a745',
            color: '#fff',
            cursor: 'pointer'
          }}
        >
          Get Started
        </button>
      )}
    </div>
  );
}

export default Welcome;
